import { View, Text, Image, ScrollView } from 'react-native'
import React from 'react'

const items = [
    {
        Image: require("../../../assets/bakery-icon.png"),
        text: "Bakery"
    },
    {
        Image: require("../../../assets/food-icon.png"),
        text: "Food"
    },
    {
        Image: require("../../../assets/clothes-icon.png"),
        text: "Clothes"
    },
    {
        Image: require("../../../assets/others-icon.webp"),
        text: "Others"
    },
];

export default function Categories() {
  return (
    <ScrollView horizontal showsHorizontalScrollIndicator = { false }>
    {/* loop starts here */}
    {items.map((item, index) => (
    <View style = {{ alignItems: "center", marginRight: 30}}>
        <Image 
        source = {items[0].Image} 
        style = {{
            width: 50,
            height: 40,
            resizeMode: "contain",
      }}/>
      <Text style = {{ fontSize: 13, fontWeight: "700"}}>{items[0].text}</Text>
      </View>
      ))}
      {/* loop ends here */}
    </ScrollView>
  )
}
