import { View, Text, Image, TouchableOpacity } from 'react-native'
import React from 'react'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'

export default function Products() {
  return (
    <View style = {{marginTop: 10, padding: 15, backgroundColor: ""}}>
      <ProductImage/>
      <ProductInfo/>
    </View>
  )
}

const ProductImage = () => (
    <>
    <Image
        source = {{
            uri: "https://hips.hearstapps.com/del.h-cdn.co/assets/16/38/1600x800/landscape-1474650684-cakes-group-193.jpg?resize=980:*"
        }}
        style = {{ width: "100%", height: 180 }}/>
    <TouchableOpacity style = {{position: 'absolute', right: 20, top: 20}}>
        <MaterialCommunityIcons name = "heart-outline" size = {25} color = "white"/>
    </TouchableOpacity>
    </>
)

const ProductInfo = () => (
    <View>
        <Text style = {{fontSize: 18, fontWeight: "bold"}}>Amy's cake shop</Text>
        <Text style = {{fontSize: 13, color: "gray"}}>30-45 min</Text>
    </View>
)
